mapper.update:
 update message
set status = 1,
modify_id = #{modifyId},
modify_time = #{modifyTime}
where id=#{smsId}

update message
set data_file = #{dataFile,jdbcType=VARCHAR}
  where id=#{smsId}

mapper.select:
select
,

from message

  status = #{smsStatus,jdbcType=VARCHAR}

    AND create_id = #{createId}


    AND id=#{smsId}


     and Date(create_time) >= #{saveTimeStart}


     and Date(create_time) <= #{saveTimeEnd}


     and Date(modify_time) >= #{submitTimeStart} 


     and Date(modify_time) <= #{submitTimeEnd} 


     and  Date(approvalTime)>= #{approvalTimeStart}


     and Date(approvalTime) <= #{approvalTimeEnd}




    ORDER BY create_time DESC


    ORDER BY approval_time DESC


    ORDER BY modify_time DESC

select
count(id)
from message

  1 = 1

    and create_id = #{createId}


    and status = #{status}
